mapper.select:
select order_no as orderNo, member_id as memberId, p_no as pNo, order_date as orderDate, order_quan as orderQuan, order_status as orderStatus, shipping_phone as shippingPhone, shipping_addr as shippingAddr, (select p_name from market_product mp where ot.p_no = mp.p_no) as pName, (select p_price from market_product mp where ot.p_no = mp.p_no) as pPrice, (select owner_no from market_product mp where ot.p_no = mp.p_no) as ownerNo from (select rownum as rnum, n.* from (select * from order_tbl order by order_date desc) n) ot where (select owner_no from market_product mp where ot.p_no = mp.p_no) = #{ownerNo} and rnum between #{start} and #{end}
and order_no like '%'||#{keyword}||'%'
and member_id like '%'||#{keyword}||'%'

select count(*) from order_tbl ot where (select owner_no from market_product mp where ot.p_no = mp.p_no) = #{ownerNo}
and order_no like '%'||#{keyword}||'%'
and member_id like '%'||#{keyword}||'%'

mapper.update:
update order_tbl ot
set order_status =#{orderStatus}
where order_no = #{orderNo}
